const Example = () => {
  return (
    <div>
      <label htmlFor="my-check">
        チェック：
        </label>
        <input
          type="checkbox"
          id="my-check"
          checked={isChecked}
          onChange={(e) => toggleChecked(e)}
        />
        <div>{isChecked ? "ON!" : "OFF!"}</div>
    </div>
  );
};

export default Example;

