import { useState } from "react";

const animals = ["Dog", "Cat", "Rat"];

const Example = () => {
  const [filterVal, setFilterval] = useState("");

  return (
    <>
      <h3>配列のフィルター</h3>
      <input type="text" value={filterval}/>
      <ul>
        {animals
          .map((animal) => (
          <li>{animal}</li>
        ))}
      </ul>
    </>
  );
};

export default Example;

