import { useState } from "react";

const Example = () => {

  const [fruit, setFruit] = useState("Apple");
  const onChange = (e) => setFruit(e.target.value);

  return (
    <>
      <label>
        <input
          type="radio"
          value="Apple"
          checked={fruit === "Apple"}
          onChange={onChange}
        />
      </label>
    </>
  );
};

export default Example;


